import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import '../App.css'
import axios from 'axios'

class MemoryCard extends Component {
  constructor (props) {
    super(props)
    this.state = {
      memory: {}
    }
  }

  componentDidMount () {
    // console.log("Print id: " + this.props.match.params.id);
    axios
      .get('http://localhost:5000/memories/' + this.props.match.params.id)
      .then(res => {
        // console.log("Print-showBookDetails-API-response: " + res.data);
        this.setState({
          memory: res.data
        })
      })
      .catch(err => {
        console.log('Error from ShowBookDetails')
      })
  }

  onDeleteClick (id) {
    axios
      .delete('http://localhost:5000/memories/' + id)
      .then(res => {
        this.props.history.push('/')
      })
      .catch(err => {
        console.log('Error form ShowBookDetails_deleteClick')
      })
  }

  render () {
    const memory = this.state.memory
    let MemoryItem = (
      <div>
        
      </div>
    )

    return (
      <div className='ShowBookDetails'>
        <div className='container'>
          <div className='row'>
            <div className='col-md-10 m-auto'>
              <br /> <br />
              <Link to='/' className='btn btn-outline-warning float-left'>
                Show Book List
              </Link>
            </div>
            <br />
            <div className='col-md-8 m-auto'>
              <h1 className='display-4 text-center'> Record</h1>
              <p className='lead text-center'>View  Info</p>
              <hr /> <br />
            </div>
          </div>
          <div>{MemoryItem}</div>

          <div className='row'>
            <div className='col-md-6'>
              <button
                type='button'
                className='btn btn-outline-danger btn-lg btn-block'
                onClick={this.onDeleteClick.bind(this, memory._id)}
              >
                Delete Memories
              </button>
              <br />
            </div>

            <div className='col-md-6'>
              <Link
                to={`/edit-memories/${memory._id}`}
                className='btn btn-outline-info btn-lg btn-block'
              >
                Edit Memories
              </Link>
              <br />
            </div>
          </div>
          {/* <br />
            <button type="button" class="btn btn-outline-info btn-lg btn-block">Edit Book</button>
            <button type="button" class="btn btn-outline-danger btn-lg btn-block">Delete Book</button> */}
        </div>
      </div>
    )
  }
}

export default MemoryCard
