  render () {
    // const { title, creator, message, productImage } = this.state
    return (
      <div className='CreateBook'>
        <div className='container'>
          <div className='row'>
            <div className='col-md-8 m-auto'>
              <br />
              <Link to='/' className='btn btn-outline-warning float-left'>
                Show Memories List
              </Link>
            </div>
            <div className='col-md-8 m-auto'>
              <h1 className='display-4 text-center'>Add Memories</h1>
              <p className='lead text-center'>Create new Memories</p>

              <form noValidate onSubmit={this.saveHandler}>
                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Title of the Memories'
                    name='title'
                    className='form-control'
                    value={this.title}
                    onChange={this.onChange}
                  />
                </div>
                <br />

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Creator'
                    name='creator'
                    className='form-control'
                    value={this.creator}
                    onChange={this.onChange}
                  />
                </div>

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Message'
                    name='message'
                    className='form-control'
                    value={this.message}
                    onChange={this.onChange}
                  />
                </div>
                <div className='form-group'>
                  <input
                    type='file'
                    placeholder='file'
                    name='productImage'
                    className='form-control'
                    value={this.productImage}
                    onChange={this.onChange2}
                  />
                </div>

                <input
                  type='submit'
                  className='btn btn-outline-warning btn-block mt-4'
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    )
  }
}

export default CreateMemories
