import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import '../App.css'
// import FileBase from 'react-file-base64'
import axios from 'axios'

class CreateMemories extends Component {
  state = {
    title: '',
    creator: '',
    message: '',
    productImage: null
  }
  fileSelected = event => {
    this.setState({
      title: event.target.value,
      creator: event.target.value,
      message: event.target.value,
      productImage: event.target.files[0]
    })
  }

  fileUploadHandler = () => {
    const fd = new FormData()
    fd.append('image', this.state.productImage, this.state.productImage.name)
    console.log(fd)
    axios.post('http://localhost:5000/memories', fd).then(response => {
      console.log(response)
    })
  }

  render () {
    return (
      <div>
        <form noValidate onSubmit={this.fileUploadHandler}>
                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Title of the Memories'
                    name='title'
                    className='form-control'
                    value={this.title}
                    onChange={this.onChange}
                  />
                </div>
                <br />

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Creator'
                    name='creator'
                    className='form-control'
                    value={this.creator}
                    onChange={this.onChange}
                  />
                </div>

                <div className='form-group'>
                  <input
                    type='text'
                    placeholder='Message'
                    name='message'
                    className='form-control'
                    value={this.message}
                    onChange={this.onChange}
                  />
                </div>
                <div className='form-group'>
                  <input
                    type='file'
                    placeholder='img'
                    name='productImage'
                    className='form-control'
                    value={this.state.productImage}
                    onChange={this.onChange}
                  />
                </div>

                <input
                  type='submit'
                  className='btn btn-outline-warning btn-block mt-4'
                />
              </form>
        {/* <input type='file' onChange={this.fileSelected}></input>
        <button onClick={this.fileUploadHandler}>upload</button> */}
      </div>
    )
  }
}
export default CreateMemories

